mapper.select:
select * from air_ticket_name_list where team_number in
#{item}
order by team_number

select

from air_ticket_name_list


		and ticket_order_pk = #{ticket_order_pk,jdbcType=CHAR}


		and team_number = #{team_number,jdbcType=VARCHAR}


		and order_number = #{order_number,jdbcType=VARCHAR}

select A.team_number, A.client_number, A.order_number, A.comment, B.user_name AS client_name, CONCAT(A.first_start_city,'--',A.first_end_city) AS first_from_to, A.first_ticket_date, A.first_start_city, A.first_end_city, A.ticket_order_pk, A.name, A.id, A.sale_product_pk, A.create_user, A.pk, A.update_user, A.create_time, A.update_time, A.status, C.name_confirm_status, C.days, E.comment AS need_comment
from air_ticket_name_list A LEFT JOIN user_base B ON A.client_number= B.user_number LEFT JOIN budget_order_view C ON A.team_number=C.team_number LEFT JOIN air_ticket_order E ON A.ticket_order_pk = E.pk
and B.user_name like CONCAT('%',#{params.bo.client_name,jdbcType=VARCHAR},'%')
and A.team_number like CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')
and A.order_number like CONCAT('%',#{params.bo.order_number,jdbcType=VARCHAR},'%')
and A.name like CONCAT('%',#{params.bo.name,jdbcType=VARCHAR},'%')
and A.status=#{params.bo.status,jdbcType=CHAR}
and A.first_ticket_date>=#{params.bo.date_from,jdbcType=CHAR}
and A.first_ticket_date<=#{params.bo.date_to,jdbcType=CHAR}
ORDER BY A.order_number,A.team_number,ticket_order_pk,A.first_ticket_date
