mapper.select:
SELECT SUM(A.money) AS money, A.product_manager AS product_manager_number, B.user_name AS product_manager_name, A.belong_month AS belong_month FROM air_ticket_paid_detail A LEFT JOIN user_base B ON A.product_manager = B.user_number
and A.type = 'DEDUCT'
and A.belong_month = #{first_month,jdbcType=VARCHAR}
and LEFT(A.belong_month,4) = #{first_year,jdbcType=VARCHAR}
and A.product_manager = #{product_manager_number,jdbcType=VARCHAR}
GROUP BY A.product_manager,A.belong_month ORDER BY money DESC

SELECT SUM(IF(type='RECEIVE',money,0)) AS receive_money, SUM(IF(type='PAY',money,0)) AS pay_money, SUM(IF(type='RECEIVE',-money,money)) AS money, B.user_name AS product_manager_name FROM air_ticket_paid_detail A LEFT JOIN user_base B ON A.product_manager = B.user_number
and (type = 'RECEIVE' OR type = 'PAY')
and belong_month = #{first_month,jdbcType=VARCHAR}
and product_manager = #{product_manager_number,jdbcType=VARCHAR}
GROUP BY product_manager ORDER BY money DESC

select B.financial_body_pk, C.supplier_name AS financial_body_name, B.name AS supplier_employee_name, A.money, A.allot_money, A.sum_money, A.status, A.type, A.time, A.limit_time, A.confirm_time, A.approve_user, A.related_pk, A.comment, A.pk, A.voucher_number, A.create_user, A.update_user, A.base_pk, A.PNR, A.create_time, A.update_time, COALESCE(C.supplier_name, A.receiver) AS receiver, D.first_date, D.passenger, D.from_to_city, A.product_manager, E.user_name AS product_manager_name, A.belong_month from air_ticket_paid_detail A LEFT JOIN supplier_employee B ON A.supplier_employee_pk = B.pk LEFT JOIN supplier C ON B.financial_body_pk = C.pk LEFT JOIN air_ticket_payable D ON A.base_pk = D.pk LEFT JOIN user_base E ON A.product_manager = E.user_number
and A.status in
#{params.bo.statuses[${index}]}
and LEFT(A.time,10) >= #{params.bo.date_from,jdbcType=VARCHAR}
and LEFT(A.time,10) <= #{params.bo.date_to,jdbcType=VARCHAR}
and A.belong_month = #{params.bo.belong_month,jdbcType=VARCHAR}
and A.product_manager = #{params.bo.product_manager,jdbcType=VARCHAR}
and A.money >= #{params.bo.money_from,jdbcType=DECIMAL}
and A.money <= #{params.bo.money_to,jdbcType=DECIMAL}
and A.money = #{params.bo.money,jdbcType=DECIMAL}
and COALESCE(C.supplier_name, A.receiver) like CONCAT('%',#{params.bo.receiver,jdbcType=VARCHAR},'%')
and A.type =#{params.bo.type,jdbcType=VARCHAR}
and D.from_to_city like CONCAT('%',#{params.bo.from_to_city,jdbcType=VARCHAR},'%')
and D.first_date >= #{params.bo.first_date_from,jdbcType=VARCHAR}
and D.first_date <= #{params.bo.first_date_to,jdbcType=VARCHAR}
and A.voucher_number like CONCAT('%',#{params.bo.voucher_number,jdbcType=VARCHAR},'%')
order by D.first_date DESC,A.time DESC
