SELECT payable_pk, team_number, SUM(current_people_count) AS people_count, LEFT(first_date, 7) AS first_month, SUM(per_team_fee) AS payable, SUM(per_team_paid) AS paid, product_manager_name, product_manager_number, sale_number, sale_name FROM view_air_service_fee
and LEFT(first_date, 7) = #{first_month,jdbcType=VARCHAR}
and LEFT(first_date, 4) = #{first_year,jdbcType=VARCHAR}
and product_manager_number = #{product_manager_number,jdbcType=VARCHAR}
GROUP BY product_manager_number,first_month ORDER BY payable DESC

SELECT COUNT(A.pk) AS ticket_count, S.supplier_name AS supplier_name, LEFT(A.ticket_date, 7) AS first_month, A.from_to_city, COUNT(IF(C.pk IS NULL, NULL, B.pk)) AS change_cnt, D.budget_payable AS sum_fee, D.paid AS paid_fee FROM passenger_ticket_info A LEFT JOIN supplier_employee B ON ticket_source_pk = B.pk LEFT JOIN supplier S ON B.financial_body_pk = S.pk LEFT JOIN air_ticket_name_list C ON A.passenger_pk = C.pk AND C.status = 'C' LEFT JOIN (SELECT SUM(A.budget_payable) AS budget_payable, SUM(A.paid) AS paid, B.financial_body_pk AS supplier_pk, LEFT(A.first_date, 7) AS ticket_month, A.from_to_city FROM air_ticket_payable A LEFT JOIN supplier_employee B ON A.supplier_employee_pk = B.pk WHERE A.payable_type = 'B' GROUP BY B.financial_body_pk , LEFT(A.first_date, 7) , A.from_to_city) D ON B.financial_body_pk = D.supplier_pk AND LEFT(A.ticket_date, 7) = D.ticket_month AND A.from_to_city = D.from_to_city
and A.ticket_index = 1
and LEFT(A.ticket_date,7) = #{first_month,jdbcType=VARCHAR}
GROUP BY S.pk , LEFT(A.ticket_date, 7) , A.from_to_city;
