mapper.select:
select

from client_received_detail


		and type= #{type,jdbcType=VARCHAR}


		and team_number= #{team_number,jdbcType=VARCHAR}


		and LEFT(confirm_time,10) <= #{confirm_time_end,jdbcType=VARCHAR}

select sum(received) from
client_received_detail where type != 'TAIL98'
and team_number = #{team_number,jdbcType=CHAR}

select A.team_number, A.received, A.received_time, A.type, A.confirm_time, A.status, A.comment, A.sum_received, A.allot_received, A.related_pk, A.card_pk, A.card_account, A.create_user, A.update_user, A.pk, C.user_name AS user_name, A.create_time, A.update_time, D.name AS client_employee_name, A.client_employee_pk, A.voucher_file from received_detail A LEFT JOIN user_base C ON A.create_user=C.user_number LEFT JOIN client_employee D ON A.client_employee_pk = D.pk
and A.status in
#{params.bo.statuses[${index}]}
and A.create_user = #{params.bo.create_user,jdbcType=VARCHAR}
and LEFT(A.received_time,7) = #{params.bo.month,jdbcType=VARCHAR}
and LEFT(A.received_time,10) >= #{params.bo.date_from,jdbcType=VARCHAR}
and LEFT(A.received_time,10) <= #{params.bo.date_to,jdbcType=VARCHAR}
and A.card_account = #{params.bo.card_account,jdbcType=VARCHAR}
and if(A.type='SUM', A.allot_received >= #{params.bo.money_from,jdbcType=DECIMAL},A.received >= #{params.bo.money_from,jdbcType=DECIMAL})
and if(A.type='SUM', A.allot_received <= #{params.bo.money_to,jdbcType=DECIMAL},A.received <= #{params.bo.money_to,jdbcType=DECIMAL})
and if(A.type='SUM', A.allot_received = #{params.bo.money,jdbcType=DECIMAL},A.received = #{params.bo.money,jdbcType=DECIMAL})
and A.type in
#{params.bo.types[${index}]}
and A.team_number like concat('%', #{params.bo.team_number,jdbcType=VARCHAR},'%')
and D.name like concat('%', #{params.bo.client_employee_name,jdbcType=VARCHAR},'%')
ORDER BY A.create_time DESC, A.received_time
