mapper.select:
SELECT X.team_number, X.discount_receivable, X.discount_flg, X.sale_cost, X.sys_cost, X.approved, IF(A.independent_flg='A',CONCAT(E.from_city,'--',E.to_city,'（单）'),A.product_name) AS product_name, A.client_employee_pk, A.client_employee_name, A.other_cost, A.fy, IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable) AS receivable, A.air_ticket_cost, C.payable AS product_cost, A.departure_date, A.pk, A.confirm_flg AS order_type, IF(A.confirm_flg = 'F', D.adult_count, A.adult_count)+IF(A.confirm_flg = 'F', IF(ISNULL(D.special_count), 0, D.special_count), IF(ISNULL(A.special_count), 0, A.special_count)) AS people_count, A.update_user, A.create_user_number AS sale_number, A.create_user AS sale_name, IF(X.discount_flg = 'Y', X.discount_receivable, IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable)) - IF(ISNULL(A.air_ticket_cost), 0, A.air_ticket_cost) - IF(ISNULL(A.other_cost), 0, A.other_cost) - IF(ISNULL(C.payable), 0, C.payable) - IF(ISNULL(A.fy), 0, A.fy) - X.sys_cost-X.sale_cost AS gross_profit, (IF(X.discount_flg = 'Y', X.discount_receivable, IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable)) - IF(ISNULL(A.air_ticket_cost), 0, A.air_ticket_cost) - IF(ISNULL(A.other_cost), 0, A.other_cost) - IF(ISNULL(C.payable), 0, C.payable) - IF(ISNULL(A.fy), 0, A.fy)- X.sys_cost-X.sale_cost) / (IF(A.confirm_flg = 'F', D.adult_count, A.adult_count)+IF(A.confirm_flg = 'F', IF(ISNULL(D.special_count), 0, D.special_count), IF(ISNULL(A.special_count), 0, A.special_count))) AS per_profit, A.confirm_flg, A.confirm_date, C.final_flg AS product_final_flg, A.independent_flg FROM team_report X LEFT JOIN budget_order_view A ON A.team_number = X.team_number LEFT JOIN receivable B ON A.team_number = B.team_number LEFT JOIN view_team_payable C ON A.team_number = C.team_number LEFT JOIN final_order_view D ON A.team_number=D.team_number LEFT JOIN sale_order_ticket_info E ON A.pk=E.order_pk and E.ticket_index=1
and A.create_user_number like CONCAT('%', #{params.bo.sale_number,jdbcType=VARCHAR},'%')
and A.team_number like CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')
and (A.product_name like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%') OR CONCAT(E.from_city,'--',E.to_city) like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%'))
and A.departure_date >= #{params.bo.departure_date_from,jdbcType=VARCHAR}
and A.departure_date <= #{params.bo.departure_date_to,jdbcType=VARCHAR}
and A.confirm_date >= #{params.bo.confirm_date_from,jdbcType=VARCHAR}
and A.confirm_date <= #{params.bo.confirm_date_to,jdbcType=VARCHAR}
and A.product_manager_number = #{params.bo.product_manager_number,jdbcType=VARCHAR}
and X.approved in
#{params.bo.report_statuses[${index}]}
and (A.confirm_flg='Y' or A.confirm_flg='F') and (D.final_type !=4 or D.final_type is null)
ORDER BY A.departure_date DESC

SELECT SUM(IF(X.discount_flg = 'Y', X.discount_receivable, 0)) AS discount_receivable, SUM(IF(X.discount_flg = 'Y', IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable) - X.discount_receivable, 0)) AS tail98, SUM(X.sale_cost) AS sale_cost, SUM(X.sys_cost) AS sys_cost, SUM(A.other_cost) AS other_cost, SUM(A.fy) AS fy, SUM(IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable)) AS receivable, SUM(A.air_ticket_cost) AS air_ticket_cost, SUM(C.payable) AS product_cost, SUM(IF(A.confirm_flg = 'F', D.adult_count, A.adult_count)) AS adult_count, SUM(IF(A.confirm_flg = 'F', IF(ISNULL(D.special_count), 0, D.special_count), IF(ISNULL(A.special_count), 0, A.special_count))) AS special_count, SUM(IF(X.discount_flg = 'Y', X.discount_receivable, IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable)) - IF(ISNULL(A.air_ticket_cost), 0, A.air_ticket_cost) - IF(ISNULL(A.other_cost), 0, A.other_cost) - IF(ISNULL(C.payable), 0, C.payable) - IF(ISNULL(A.fy), 0, A.fy) - X.sys_cost - X.sale_cost) AS gross_profit, SUM(IF(X.discount_flg = 'Y', X.discount_receivable, IF(B.final_flg = 'Y', B.final_receivable, B.budget_receivable)) - IF(ISNULL(A.air_ticket_cost), 0, A.air_ticket_cost) - IF(ISNULL(A.other_cost), 0, A.other_cost) - IF(ISNULL(C.payable), 0, C.payable) - IF(ISNULL(A.fy), 0, A.fy) - X.sys_cost - X.sale_cost) / SUM(IF(A.confirm_flg = 'F', D.adult_count, A.adult_count) + IF(A.confirm_flg = 'F', IF(ISNULL(D.special_count), 0, D.special_count), IF(ISNULL(A.special_count), 0, A.special_count))) AS per_profit FROM team_report X LEFT JOIN budget_order_view A ON A.team_number = X.team_number LEFT JOIN receivable B ON A.team_number = B.team_number LEFT JOIN view_team_payable C ON A.team_number = C.team_number LEFT JOIN final_order_view D ON A.team_number = D.team_number
and A.departure_date >= #{date_from,jdbcType=VARCHAR}
and A.departure_date <= #{date_to,jdbcType=VARCHAR}
and A.product_manager_number = #{product_manager_number,jdbcType=VARCHAR}
and X.approved in
#{report_statuses[${index}]}
and (A.confirm_flg='Y' or A.confirm_flg='F') and (D.final_type !=4 or D.final_type is null)
