mapper.select:
select A.team_number, A.final_flg, A.supplier_employee_name, A.supplier_employee_pk, A.departure_date, A.return_date, A.product, A.people_count, A.budget_payable, A.final_payable, A.paid, A.budget_balance, A.final_balance, A.sales, A.sales_name, A.update_user, A.pk, A.create_user, A.create_time, A.update_time, B.financial_body_name as supplier_name, B.financial_body_pk as supplier_pk
from payable A LEFT JOIN supplier_employee B on A.supplier_employee_pk = B.pk
and A.team_number = #{team_number,jdbcType=VARCHAR}
and A.supplier_employee_pk = #{supplier_employee_pk,jdbcType=VARCHAR}

select A.team_number, A.final_flg, B.name AS supplier_employee_name, A.supplier_employee_pk, A.departure_date, A.return_date, A.product, A.people_count, A.budget_payable, TIMESTAMPDIFF(day,A.return_date,now())+1 AS back_days, A.final_payable, A.paid, A.budget_balance, A.final_balance, A.sales, C.user_name AS sales_name, A.update_user, A.pk, A.create_user, A.create_time, A.update_time, D.supplier_short_name AS supplier_name, B.financial_body_pk as supplier_pk, IF(ISNULL(E.pick_date),A.departure_date,E.pick_date) AS pick_date from payable A LEFT JOIN supplier_employee B on A.supplier_employee_pk = B.pk LEFT JOIN user_base C ON A.sales = C.user_number LEFT JOIN supplier D ON B.financial_body_pk = D.pk LEFT JOIN product_order_operation E ON A.team_number= E.team_number and A.supplier_employee_pk = E.supplier_employee_pk
and B.name LIKE CONCAT('%', #{params.bo.supplier_employee_name,jdbcType=VARCHAR},'%')
and C.user_name LIKE CONCAT('%', #{params.bo.sales_name,jdbcType=VARCHAR},'%')
and (A.sales = #{params.bo.sales,jdbcType=VARCHAR} or A.create_user = #{params.bo.create_user,jdbcType=VARCHAR})
and D.supplier_short_name LIKE CONCAT('%', #{params.bo.supplier_name,jdbcType=VARCHAR},'%')
and A.team_number LIKE CONCAT('%', #{params.bo.team_number,jdbcType=VARCHAR},'%')
and LEFT(IF(ISNULL(E.pick_date),A.departure_date,E.pick_date),7) = #{params.bo.departure_month,jdbcType=VARCHAR}
and A.departure_date > #{params.bo.departure_from,jdbcType=VARCHAR}
and A.departure_date <= #{params.bo.departure_to,jdbcType=VARCHAR}
and A.return_date <= #{params.bo.return_to,jdbcType=VARCHAR}
and if(final_flg='N',budget_balance!=0,final_balance!=0)
ORDER BY pick_date DESC
ORDER BY pick_date ASC
