mapper.select:
select

from product


		and name = #{name,jdbcType=VARCHAR}


		and product_model = #{product_model,jdbcType=VARCHAR}


		and create_user = #{create_user,jdbcType=VARCHAR}


		and location = #{location,jdbcType=VARCHAR}


		and keep_flg = #{keep_flg,jdbcType=VARCHAR}

select
from product where pk in
#{item}

select A.name, A.product_number, A.location, A.days, A.max_profit_substract, A.business_price, B.user_name AS product_manager, A.sale_flg, A.comment, A.pk, A.create_user, A.update_user, A.air_ticket_charge, A.product_value, A.air_ticket_cost, A.other_cost, A.gross_profit, A.gross_profit_rate, A.product_model, A.adult_price, A.child_price, A.business_profit_substract, A.air_ticket_child_cost, A.local_adult_cost, A.local_child_cost, A.other_child_cost, A.product_child_value, A.cash_flow, A.spot_cash, A.first_air_start, A.first_air_end, A.sale_attention, A.sale_strategy, A.strict_price_flg, A.cash_flow_air_flg, A.cash_flow_local_flg, A.cash_flow_other_flg, A.create_time, A.update_time, A.off_shelves_date, A.gross_child_profit, A.cash_child_flow, A.spot_child_cash, A.gross_child_profit_rate, A.client_confirm_templet, A.out_notice_templet, A.air_ticket_upkeep_flg, A.supplier_upkeep_flg, A.local_upkeep_flg, A.keep_flg, A.analysis_flg, A.as_adult_flg, A.id_type from product A LEFT JOIN user_base B on A.product_manager=B.user_number
and A.product_manager = #{params.bo.product_manager,jdbcType=VARCHAR}
and A.product_number like concat('%', #{params.bo.product_number,jdbcType=VARCHAR},'%')
and A.product_model like concat('%', #{params.bo.product_model,jdbcType=VARCHAR},'%')
and A.name like concat('%', #{params.bo.name,jdbcType=VARCHAR},'%')
and A.location = #{params.bo.location,jdbcType=VARCHAR}
and A.location in
#{params.bo.locations[${index}]}
and A.sale_flg in
#{params.bo.statuses[${index}]}
and A.sale_flg = #{params.bo.sale_flg,jdbcType=VARCHAR}
and A.product_value >= 9
order by A.product_value DESC,A.name,A.product_model
