mapper.select:
select A.team_number, A.operate_index, A.supplier_count, A.supplier_employee_pk, B.name AS supplier_employee_name, A.supplier_cost, A.final_supplier_cost, A.supplier_product_name, A.people_count, A.pick_date, A.send_date, A.pick_type, A.picker_cellphone, A.send_type, A.create_user, A.update_user, A.pk, A.create_time, A.update_time, A.status, A.picker, A.land_day, A.off_day, C.supplier_short_name AS supplier_name, C.pk AS supplier_pk, substring_index(D.name_list,':',1) AS passenger from product_order_operation A LEFT JOIN supplier_employee B ON A.supplier_employee_pk = B.pk LEFT JOIN supplier C ON B.financial_body_pk = C.pk LEFT JOIN budget_order_view D ON A.team_number = D.team_number
and A.create_user = #{params.bo.create_user,jdbcType=VARCHAR}
and A.status = #{params.bo.status,jdbcType=VARCHAR}
and A.team_number LIKE CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')
and A.supplier_product_name LIKE CONCAT('%',#{params.bo.supplier_product_name,jdbcType=VARCHAR},'%')
and B.name LIKE CONCAT('%',#{params.bo.supplier_employee_name,jdbcType=VARCHAR},'%')
and C.supplier_short_name LIKE CONCAT('%',#{params.bo.supplier_name,jdbcType=VARCHAR},'%')
and LEFT(A.pick_date,7) = #{params.bo.pick_month,jdbcType=VARCHAR}
ORDER BY A.pick_date,A.team_number,A.operate_index DESC
