changeSet.sql:
drop view if exists accurate_sale_summary; create view accurate_sale_summary as select client_employee_pk, count(*) as accurate_count, datediff(now(), max(date)) last_accurate_period from accurate_sale group by client_employee_pk;
DROP VIEW IF EXISTS client_relation_summary; CREATE VIEW client_relation_summary AS select A.name as client_employee_name, A.pk as client_employee_pk, A.sales, A.sales_name, A.delete_flg as delete_flg, A.relation_level as relation_level, A.back_level as back_level, A.market_level as market_level, case when isnull(B.year_order_count) then 0 else B.year_order_count end year_order_count, case when isnull(C.month_order_count) then 0 else C.month_order_count end month_order_count, case when isnull(D.week_order_count) then 0 else D.week_order_count end week_order_count, E.last_confirm_date, E.last_order_period, F.visit_count, F.last_visit_period, G.chat_count, G.last_chat_period, H.receivable, H.last_receivable_period, I.accurate_count from client_employee A LEFT JOIN year_order_count B ON A.pk = B.client_employee_pk LEFT JOIN month_order_count C ON A.pk = C.client_employee_pk LEFT JOIN week_order_count D ON A.pk = D.client_employee_pk LEFT JOIN order_period E ON A.pk = E.client_employee_pk LEFT JOIN visit_summary F ON A.pk = F.client_employee_pk LEFT JOIN chat_summary G ON A.pk = G.client_employee_pk LEFT JOIN receivable_for_client H ON A.pk = H.client_employee_pk LEFT JOIN accurate_sale_summary I ON A.pk = I.client_employee_pk;
CREATE TABLE accurate_sale

DROP VIEW IF EXISTS month_order_count; CREATE VIEW month_order_count AS select A.client_employee_pk as client_employee_pk, count(A.client_employee_pk) as month_order_count, B.relation_level as relation_level, B.back_level as back_level, B.market_level as market_level, LEFT(A.confirm_date, 7) as month from budget_order A LEFT JOIN client_employee B on A.client_employee_pk=B.pk
group by A.client_employee_pk,month;
ALTER VIEW month_order_count

DROP VIEW IF EXISTS week_order_count; CREATE VIEW week_order_count AS select A.client_employee_pk as client_employee_pk, count(A.client_employee_pk) as week_order_count, B.relation_level as relation_level, B.back_level as back_level, B.market_level as market_level, '本周' as week_no from budget_order A LEFT JOIN client_employee B on A.client_employee_pk=B.pk where confirm_date >= DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) DAY) and confirm_date <= DATE_SUB(curdate(),INTERVAL WEEKDAY(curdate()) - 6 DAY) group by A.client_employee_pk
UNION ALL select A.client_employee_pk as client_employee_pk, count(A.client_employee_pk) as week_order_count, B.relation_level as relation_level, B.back_level as back_level, B.market_level as market_level, '上周' as week_no from budget_order A LEFT JOIN client_employee B on A.client_employee_pk=B.pk where confirm_date >=DATE_SUB(CURDATE(),INTERVAL WEEKDAY(CURDATE())+7 DAY) and confirm_date <= DATE_SUB(CURDATE(),INTERVAL WEEKDAY(CURDATE())+1 DAY) group by A.client_employee_pk UNION ALL select A.client_employee_pk as client_employee_pk, count(A.client_employee_pk) as week_order_count, B.relation_level as relation_level, B.back_level as back_level, B.market_level as market_level, '上上周' as week_no from budget_order A LEFT JOIN client_employee B on A.client_employee_pk=B.pk where confirm_date >= DATE_SUB(CURDATE(),INTERVAL WEEKDAY(CURDATE())+14 DAY) and confirm_date <= DATE_SUB(CURDATE(),INTERVAL WEEKDAY(CURDATE())+8 DAY) group by A.client_employee_pk;
ALTER VIEW week_order_count
