mapper.select:
select A.client_name, A.client_short_name, A.client_area, A.client_county, A.client_type, A.telephone, A.fax, A.address, A.body_name, A.body_sex, A.body_id, A.body_birth_year, A.body_wechat, A.body_cellphone, A.create_user, A.update_user, A.approve_user, A.update_reason, A.comment, A.pk, A.public_flg, A.create_time, A.update_time, A.delete_flg, A.agency_name, A.agency_pk, A.relate_flg, A.main_business, A.store_type, A.back_level, A.market_level, A.talk_level from client A LEFT JOIN client_user B ON A.pk = B.client_pk
and B.user_pk = #{sales,jdbcType=VARCHAR}
and A.client_name = #{client_name,jdbcType=VARCHAR}

select A.client_name, A.client_short_name, A.client_area, A.client_county, A.client_type, A.telephone, A.fax, A.address, A.body_name, A.body_sex, A.body_id, A.body_birth_year, A.body_wechat, A.body_cellphone, A.create_user, A.update_user, A.approve_user, A.update_reason, A.comment, A.pk, A.sales, if(ISNULL(B.user_name),'公开',B.user_name) AS sales_name, A.public_flg, A.create_time, A.update_time, A.delete_flg, A.agency_name, A.agency_pk, A.relate_flg, A.main_business, A.store_type, A.back_level, A.market_level, A.talk_level, C.client_employee_count, D.sum_balance, E.client_year_order_count, F.last_order_date from client A LEFT JOIN user_base B ON A.sales = B.pk LEFT JOIN view_client_employee_count C ON A.pk = C.client_pk LEFT JOIN view_client_receivable D ON A.pk = D.client_pk LEFT JOIN view_client_year_order_count E ON A.pk = E.client_pk LEFT JOIN view_client_last_order F ON A.pk = F.client_pk LEFT JOIN client_user G ON A.pk = G.client_pk
and (G.user_pk = #{params.bo.sales,jdbcType=VARCHAR} OR A.public_flg='Y')
and A.client_name like CONCAT('%',#{params.bo.client_name,jdbcType=VARCHAR},'%')
and A.client_short_name like CONCAT('%',#{params.bo.client_short_name,jdbcType=VARCHAR},'%')
and A.address like CONCAT('%',#{params.bo.address,jdbcType=VARCHAR},'%')
and A.client_area = #{params.bo.client_area,jdbcType=VARCHAR}
and A.client_county = #{params.bo.client_county,jdbcType=VARCHAR}
and A.store_type=#{params.bo.store_type,jdbcType=VARCHAR}
and A.back_level=#{params.bo.back_level,jdbcType=VARCHAR}
and A.delete_flg in
#{params.bo.statuses[${index}]}
and A.relate_flg in
#{params.bo.relate_flgs[${index}]}
and A.public_flg in ('Y','N')
and A.public_flg ='N'
and A.talk_level in
#{params.bo.talk_levels[${index}]}
and A.main_business in
#{params.bo.main_businesses[${index}]}
ORDER BY address DESC

select A.client_name, A.client_short_name, A.client_area, A.client_county, A.client_type, A.telephone, A.fax, A.address, A.body_name, A.body_sex, A.body_id, A.body_birth_year, A.body_wechat, A.body_cellphone, A.create_user, A.update_user, A.approve_user, A.update_reason, A.comment, A.pk, A.public_flg, A.create_time, A.update_time, A.delete_flg, A.agency_name, A.agency_pk, A.relate_flg, A.main_business, A.store_type, A.back_level, A.market_level, A.talk_level, C.client_employee_count, D.sum_balance, E.client_year_order_count, F.last_order_date from client A LEFT JOIN view_client_employee_count C ON A.pk = C.client_pk LEFT JOIN view_client_receivable D ON A.pk = D.client_pk LEFT JOIN view_client_year_order_count E ON A.pk = E.client_pk LEFT JOIN view_client_last_order F ON A.pk = F.client_pk
and A.client_name like CONCAT('%',#{params.bo.client_name,jdbcType=VARCHAR},'%')
and A.client_short_name like CONCAT('%',#{params.bo.client_short_name,jdbcType=VARCHAR},'%')
and A.address like CONCAT('%',#{params.bo.address,jdbcType=VARCHAR},'%')
and A.client_area = #{params.bo.client_area,jdbcType=VARCHAR}
and A.client_county = #{params.bo.client_county,jdbcType=VARCHAR}
and A.store_type=#{params.bo.store_type,jdbcType=VARCHAR}
and A.back_level=#{params.bo.back_level,jdbcType=VARCHAR}
and A.delete_flg in
#{params.bo.statuses[${index}]}
and A.relate_flg in
#{params.bo.relate_flgs[${index}]}
and A.public_flg in ('Y','N')
and A.public_flg ='N'
and A.talk_level in
#{params.bo.talk_levels[${index}]}
and A.main_business in
#{params.bo.main_businesses[${index}]}
ORDER BY address DESC
