mapper.select:
select * from view_all_connect


		and client_employee_pk = #{params.bo.client_employee_pk,jdbcType=VARCHAR}


ORDER BY connect_date DESC

select count(A.pk) from budget_order A left JOIN user_base B on A.create_user = B.user_number
and A.confirm_date >= DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY) and A.confirm_date <=date_sub(CURDATE(),INTERVAL WEEKDAY(CURDATE()) - 6 DAY)
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from budget_order A left JOIN user_base B on A.create_user = B.user_number

	and LEFT(A.confirm_date,7)=DATE_FORMAT(NOW(),'%Y-%m')

		and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and A.date = DATE_SUB(current_date, INTERVAL 1 DAY) and A.type='VISIT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and A.date >= DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY) and A.date <=date_sub(CURDATE(),INTERVAL WEEKDAY(CURDATE()) - 6 DAY) and A.type='VISIT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and LEFT(A.date,7)=DATE_FORMAT(NOW(),'%Y-%m') and A.type='VISIT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and A.date = DATE_SUB(current_date, INTERVAL 1 DAY) and A.type='CHAT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and A.date >= DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY) and A.date <=date_sub(CURDATE(),INTERVAL WEEKDAY(CURDATE()) - 6 DAY) and A.type='CHAT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}

select count(A.pk) from client_visit A LEFT JOIN user_base B on A.create_user= B.user_number
and LEFT(A.date,7)=DATE_FORMAT(NOW(),'%Y-%m') and A.type='CHAT'
and B.user_name = #{_parameter,jdbcType=VARCHAR}
