mapper.select:
select team_number, client_employee_name, product_name, departure_date, confirm_date, adult_count+IFNULL(special_count,0) AS people_count from budget_order_view
and create_user_number= #{create_user,jdbcType=VARCHAR}
and name_confirm_status in('2','4')

SELECT A.team_number, B.create_user, B.adult_count, B.special_count, B.passenger_captain, A.budget_payable AS payable FROM payable_order A LEFT JOIN budget_order_view B ON A.team_number = B.team_number
and A.team_number in
#{item}
and A.supplier_employee_pk = #{supplier_employee_pk,jdbcType=VARCHAR}

SELECT sum(IF( A.confirm_flg!='N' AND(C.final_type is null OR C.final_type!=4),(IFNULL(A.adult_count,0)+IFNULL(A.special_count,0))*A.product_value,0)) AS score, LEFT(A.confirm_date,7) as confirm_month, A.create_user AS sale_number, B.user_name AS sale_name, sum(IF( A.confirm_flg!='N' AND(C.final_type is null OR C.final_type!=4),(IFNULL(A.adult_count,0)+IFNULL(A.special_count,0)),0)) AS sum_people FROM budget_standard_order A LEFT JOIN user_base B on A.create_user = B.user_number LEFT JOIN final_standard_order C ON A.team_number = C.team_number
and LEFT(A.confirm_date,7)= #{params.bo.confirm_month,jdbcType=VARCHAR}
and A.confirm_date >= #{params.bo.date_from,jdbcType=VARCHAR}
and A.confirm_date <= #{params.bo.date_to,jdbcType=VARCHAR}
GROUP by LEFT(A.confirm_date,7),A.create_user ORDER by score DESC

select B.user_name AS sale_name, sum(A.product_point)*0.1 AS score from view_back_money_balance A LEFT JOIN user_base B ON A.user_number = B.user_number
and LEFT(A.confirm_date,7)= #{params.bo.confirm_month,jdbcType=VARCHAR}
and A.confirm_date >= #{params.bo.date_from,jdbcType=VARCHAR}
and A.confirm_date <= #{params.bo.date_to,jdbcType=VARCHAR}
and A.balance <= (A.receivable-A.receivable*#{params.bo.discount})
and A.status = 'E'
group by A.user_number ORDER by score DESC

SELECT sum(IF( A.confirm_flg!='N' AND(C.final_type is null OR C.final_type!=4),(IFNULL(A.adult_count,0)+IFNULL(A.special_count,0))*A.product_value,0)) AS score, LEFT(A.confirm_date,7) as confirm_month, A.create_user AS sale_number, B.user_name AS sale_name, sum(IF( A.confirm_flg!='N' AND(C.final_type is null OR C.final_type!=4),(IFNULL(A.adult_count,0)+IFNULL(A.special_count,0)),0)) AS sum_people FROM budget_standard_order A LEFT JOIN user_base B on A.create_user = B.user_number LEFT JOIN final_standard_order C ON A.team_number = C.team_number
and LEFT(A.confirm_date,7)= #{confirm_month,jdbcType=VARCHAR}
and A.create_user= #{sale_number,jdbcType=VARCHAR}
GROUP by LEFT(A.confirm_date,7),A.create_user

select * from budget_order_view


		and create_user_number= #{params.bo.create_user,jdbcType=VARCHAR}


		and client_employee_name like CONCAT('%',#{params.bo.client_employee_name,jdbcType=VARCHAR},'%')


		and product_name like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%')


		and departure_date >=#{params.bo.departure_date_from,jdbcType=VARCHAR}


		and departure_date <=#{params.bo.departure_date_to,jdbcType=VARCHAR}

	and confirm_flg='N'

ORDER BY departure_date DESC

select * from budget_order_view


		and client_employee_pk =#{client_employee_pk,jdbcType=VARCHAR}


		and confirm_date =#{confirm_date,jdbcType=VARCHAR}


		and LEFT(confirm_date,4) =#{confirm_year,jdbcType=VARCHAR}


		and create_user_number =#{create_user_number,jdbcType=VARCHAR}

select A.team_number,A.departure_date,A.product_name,B.product_model,B.pk AS product_pk
from budget_order_view A LEFT
JOIN product B ON A.product_pk = B.pk


		and A.product_name =#{product_name,jdbcType=VARCHAR}


		and B.product_model =#{product_model,jdbcType=VARCHAR}


		and A.departure_date =#{departure_date,jdbcType=VARCHAR}


		and A.product_manager_number =#{product_manager_number,jdbcType=VARCHAR}


		and A.confirm_flg =#{confirm_flg,jdbcType=VARCHAR}


		and A.confirm_flg in

			#{item}

select A.team_number, A.product_name, A.product_pk, A.client_employee_pk, A.client_employee_name, A.independent_flg, A.comment, A.other_cost_comment, A.other_cost, A.fy, A.special_cost, A.special_count, A.adult_cost, A.adult_count, A.receivable, A.days, A.departure_date, A.pk, A.update_user, A.create_user_number, A.create_user, A.confirm_flg, A.create_time, A.update_time, A.confirm_date, A.confirm_file, A.standard_flg, A.air_ticket_cost, A.product_cost, IF(ISNULL(A.adult_count),0,A.adult_count)+ IF(ISNULL(A.special_count),0,A.special_count) AS people_count, A.status, TIMESTAMPDIFF(day,A.departure_date,now())-days+2 AS back_days, A.product_manager_number, A.product_manager, A.passenger_captain, A.treat_comment, A.receivable_comment, B.budget_balance AS balance, A.product_value, A.lock_flg, A.name_confirm_status from budget_order_view A LEFT JOIN receivable B ON A.team_number = B.team_number
and A.create_user_number= #{params.bo.create_user,jdbcType=VARCHAR}
and A.client_employee_name like CONCAT('%',#{params.bo.client_employee_name,jdbcType=VARCHAR},'%')
and A.team_number like CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')
and A.product_name like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%')
and A.product_manager like CONCAT('%',#{params.bo.product_manager,jdbcType=VARCHAR},'%')
and A.departure_date >=#{params.bo.departure_date_from,jdbcType=VARCHAR}
and A.departure_date <=#{params.bo.departure_date_to,jdbcType=VARCHAR}
and A.confirm_date >=#{params.bo.confirm_date_from,jdbcType=VARCHAR}
and A.confirm_date <=#{params.bo.confirm_date_to,jdbcType=VARCHAR}
and A.status in
#{params.bo.order_statuses[${index}]}
and confirm_flg='Y'
ORDER BY IF(TIMESTAMPDIFF(DAY,A.departure_date,DATE_FORMAT(NOW(), '%Y-%m-%d')) in (-1,-2,-3),A.departure_date,'2999-01-01'), IF(A.departure_date<=DATE_FORMAT(NOW(), '%Y-%m-%d') and date_add(A.departure_date,INTERVAL A.days-1 DAY)> DATE_FORMAT(NOW(), '%Y-%m-%d'),TIMESTAMPDIFF(day,A.departure_date,now())-days,-99999) DESC, IF(date_add(A.departure_date,INTERVAL A.days-1 DAY) <=DATE_FORMAT(NOW(), '%Y-%m-%d'),TIMESTAMPDIFF(DAY,A.departure_date,DATE_FORMAT(NOW(), '%Y-%m-%d')) - days + 2,0) DESC, A.departure_date

select * from final_order_view A


		and A.sale_number= #{params.bo.sale_number,jdbcType=VARCHAR}


		and A.client_employee_name like CONCAT('%',#{params.bo.client_employee_name,jdbcType=VARCHAR},'%')


		and A.team_number like CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')


		and A.product_name like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%')


		and A.departure_date >=#{params.bo.departure_date_from,jdbcType=VARCHAR}


		and A.departure_date <=#{params.bo.departure_date_to,jdbcType=VARCHAR}


		and A.confirm_date >=#{params.bo.confirm_date_from,jdbcType=VARCHAR}


		and A.confirm_date <=#{params.bo.confirm_date_to,jdbcType=VARCHAR}


ORDER BY A.departure_date DESC
