mapper.select:
select

from payment_detail

	
		and time=#{time,jdbcType=VARCHAR}
	
	
		and account = #{account,jdbcType=VARCHAR}
	

order by time

select

from payment_detail

	
		and account=#{account,jdbcType=VARCHAR}
	
	
		and time>#{time,jdbcType=VARCHAR}

select

from payment_detail

	
		and account=#{account,jdbcType=VARCHAR}
	
	
		and time <= #{time,jdbcType=VARCHAR}
	
	
		and pk != #{pk,jdbcType=VARCHAR}
	

order by time desc limit 1

select A.account, A.time, A.type, A.money, A.balance, A.record_time, A.record_user, A.update_user, A.create_user, A.pk, A.create_time, A.update_time, A.comment, A.inner_flg, A.inner_pk, A.voucher_number, A.receiver, A.voucher_file_name, A.match_flg, A.exchange_flg, A.finance_flg from payment_detail A LEFT JOIN card B ON A.account=B.account
and A.account like CONCAT('%',#{params.bo.account,jdbcType=VARCHAR},'%')
and A.comment like CONCAT('%',#{params.bo.comment,jdbcType=VARCHAR},'%')
and A.type = #{params.bo.type,jdbcType=VARCHAR}
and A.inner_flg = 'Y'
and A.match_flg = 'N'
and (A.match_flg = 'Y' OR A.match_flg='O')
and A.match_flg in
#{params.bo.match_flgs[${index}]}
and left(A.time,10) = #{params.bo.time,jdbcType=VARCHAR}
and left(A.time,7) = #{params.bo.month,jdbcType=VARCHAR}
and A.inner_flg = #{params.bo.inner_flg,jdbcType=VARCHAR}
and A.money >= #{params.bo.money_from,jdbcType=DECIMAL}
and A.money <= #{params.bo.money_to,jdbcType=DECIMAL}
and A.money = #{params.bo.money,jdbcType=DECIMAL}
and B.purpose = #{params.bo.purpose,jdbcType=DECIMAL}
and B.purpose != 'TICKET'
and date_format(from_unixtime(A.create_time/1000),'%Y-%m-%d') = #{params.bo.create_time,jdbcType=VARCHAR}
ORDER BY time desc
