mapper.select:
select
from product where pk in
#{item}

select A.name, A.product_number, A.location, A.days, A.profit_space, A.max_profit_substract, A.business_price, B.user_name AS product_manager, A.sale_flg, A.comment, A.pk, A.create_user, A.update_user, A.create_time, A.update_time, A.air_ticket_charge from product A LEFT JOIN user_base B on A.product_manager=B.user_number
and A.product_manager = #{params.bo.product_manager,jdbcType=VARCHAR}
and A.product_number like concat('%', #{params.bo.product_number,jdbcType=VARCHAR},'%')
and A.name like concat('%', #{params.bo.name,jdbcType=VARCHAR},'%')
and A.location in
#{params.bo.locations[${index}]}
and A.sale_flg = #{params.bo.sale_flg,jdbcType=VARCHAR}
order by A.create_time DESC
