mapper.select:
select IF(ISNULL(B.product_number),'非标',B.product_number) AS product_number, A.product_name, IF(ISNULL(B.product_model),'非标',B.product_model) AS product_model, CASE WHEN A.confirm_flg ='Y' THEN '已确认' WHEN A.confirm_flg = 'N' THEN '待确认' end AS status, A.team_number, A.departure_date, A.adult_count, A.special_count, A.create_user AS sale_name, A.product_manager_number, A.operate_flg, C.user_name AS product_manager, B.pk AS product_pk, B.product_number AS product_number, A.pk AS pk, CASE WHEN A.standard_flg='N' THEN substring_index(A.name_list,':',1) ELSE A.passenger_captain END AS passenger, A.treat_comment, A.lock_flg, A.name_confirm_status, A.standard_flg, CONCAT(A.start_city,'-',A.end_city) AS air_info
from budget_order_view A LEFT JOIN product B ON A.product_pk=B.pk LEFT JOIN user_base C ON A.product_manager_number = C.user_number LEFT JOIN final_order_view D ON A.team_number=D.team_number
and A.confirm_flg in ('Y','F') and D.final_type!=4
and A.product_manager_number = #{params.bo.product_manager_number,jdbcType=VARCHAR}
and B.product_model like CONCAT('%',#{params.bo.product_model,jdbcType=VARCHAR},'%')
and A.team_number like CONCAT('%',#{params.bo.team_number,jdbcType=VARCHAR},'%')
and A.product_name like CONCAT('%',#{params.bo.product_name,jdbcType=VARCHAR},'%')
and A.operate_flg in
#{params.bo.operate_flgs[${index}]}
and A.departure_date >= #{params.bo.date_from,jdbcType=VARCHAR}
and A.departure_date <= #{params.bo.date_to,jdbcType=VARCHAR}
ORDER BY A.departure_date DESC
