mapper.select:
SELECT SUM(IF(type = '支出', money, -money)) AS money, belong_month,product_manager_number FROM product_reconciliation
and product_manager_number = #{product_manager_number,jdbcType=VARCHAR}
and belong_month = #{first_month,jdbcType=VARCHAR}
GROUP BY product_manager_number , belong_month

SELECT A.pk, A.type, A.belong_month, A.money, A.product_manager_number, B.user_name AS product_manager_name, A.create_user, C.user_name AS create_user_name, A.status, A.comment FROM product_reconciliation A LEFT JOIN user_base B ON A.product_manager_number = B.user_number LEFT JOIN user_base C ON A.create_user = C.user_number
and A.product_manager_number = #{params.bo.product_manager_number,jdbcType=VARCHAR}
and A.belong_month = #{params.bo.belong_month,jdbcType=VARCHAR}
and A.money = #{params.bo.money,jdbcType=DECIMAL}
and A.money >= #{params.bo.money_from,jdbcType=DECIMAL}
and A.money <= #{params.bo.money_to,jdbcType=DECIMAL}
and A.create_user = #{params.bo.create_user,jdbcType=VARCHAR}
and A.status in
#{params.bo.statuses[${index}]}
ORDER BY belong_month DESC,product_manager_number
