mapper.select:
select

from supplier_paid_detail



		and related_pk = #{related_pk,jdbcType=VARCHAR}


		and pk = #{pk,jdbcType=CHAR}


		and team_number = #{team_number,jdbcType=VARCHAR}


		and supplier_employee_pk = #{supplier_employee_pk,jdbcType=CHAR}

select A.team_number, D.supplier_short_name as supplier_employee_name, B.financial_body_pk as supplier_employee_pk, A.money, LEFT(A.time,10) AS time, A.type, A.confirm_time, A.status, A.comment, A.sum_money, A.allot_money, A.related_pk, A.card_pk, A.card_account, C.user_name AS create_user, A.update_user, A.pk, A.create_time, A.update_time, A.limit_time from paid_detail A left join supplier_employee B on A.supplier_employee_pk=B.pk LEFT JOIN user_base C ON A.create_user = C.user_number LEFT JOIN supplier D ON B.financial_body_pk = D.pk
and A.status in
#{params.bo.statuses[${index}]}
and A.create_user like concat('%', #{params.bo.create_user,jdbcType=VARCHAR},'%')
and from_unixtime((A.create_time-0)/1000, '%Y-%m-%d') >= #{params.bo.date_from,jdbcType=VARCHAR}
and from_unixtime((A.create_time-0)/1000, '%Y-%m-%d') <= #{params.bo.date_to,jdbcType=VARCHAR}
and D.supplier_short_name like concat('%', #{params.bo.supplier_name,jdbcType=VARCHAR},'%')
